import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../store/store';
import { 
  Users, 
  Home, 
  MessageSquare, 
  Calendar, 
  TrendingUp,
  CheckCircle,
  AlertCircle,
  Clock,
  Eye,
  BarChart3,
  FileText,
  UserCheck,
  Building,
  MapPin,
  Phone,
  Mail,
  DollarSign
} from 'lucide-react';
import { getImageUrl } from '../services/config';

interface DashboardStats {
  todayListings: number;
  todayVisits: number;
  pendingVisits: number;
  activeListings: number;
  usersByRole: Array<{ role: string; count: string }>;
  recentActivity: Array<{
    type: string;
    description: string;
    timestamp: string;
    activity_type: string;
  }>;
  visitStats: {
    total_visits: string;
    scheduled_visits: string;
    completed_visits: string;
    cancelled_visits: string;
    interested_tenants: string;
    interested_owners: string;
    matches: string;
  };
  listingStats: {
    total_listings: string;
    active_listings: string;
    inactive_listings: string;
    avg_price: string;
    apartments: string;
    houses: string;
    rooms: string;
  };
  userStats: {
    total_users: string;
    tenants: string;
    owners: string;
    staff: string;
    admins: string;
    verified_users: string;
    pending_kyc: string;
  };
}

interface TodayListing {
  listing_id: string;
  title: string;
  location: string;
  price: number;
  bedrooms: number;
  bathrooms: number;
  property_type: string;
  owner_name: string;
  owner_phone: string;
  owner_email: string;
  primary_photo: string;
  visit_count: string;
  created_at: string;
}

interface TodayVisit {
  visit_id: string;
  listing_id: string;
  tenant_id: string;
  visit_datetime: string;
  status: string;
  listing_title: string;
  listing_location: string;
  listing_price: number;
  tenant_name: string;
  tenant_phone: string;
  tenant_email: string;
  owner_name: string;
  owner_phone: string;
  owner_email: string;
  listing_photo: string;
  visit_notes: string;
}

const StaffDashboardPage: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [todayListings, setTodayListings] = useState<TodayListing[]>([]);
  const [todayVisits, setTodayVisits] = useState<TodayVisit[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    fetchStaffData();
  }, []);

  const fetchStaffData = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      // Fetch dashboard stats
      const statsResponse = await fetch('http://localhost:5000/api/staff/dashboard-stats', { headers });
      const statsData = await statsResponse.json();
      setStats(statsData);

      // Fetch today's listings
      const listingsResponse = await fetch('http://localhost:5000/api/staff/today-listings', { headers });
      const listingsData = await listingsResponse.json();
      setTodayListings(listingsData.listings);

      // Fetch today's visits
      const visitsResponse = await fetch('http://localhost:5000/api/staff/today-visits', { headers });
      const visitsData = await visitsResponse.json();
      setTodayVisits(visitsData.visits);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching staff data:', error);
      setLoading(false);
    }
  };

  const quickActions = [
    {
      title: 'Manage Visits',
      description: 'Schedule and manage property visits',
      icon: Calendar,
      color: 'bg-blue-500',
      href: '/visits'
    },
    {
      title: 'Handle Messages',
      description: 'Respond to tenant inquiries',
      icon: MessageSquare,
      color: 'bg-green-500',
      href: '/messages'
    },
    {
      title: 'Verify Listings',
      description: 'Review and verify property listings',
      icon: CheckCircle,
      color: 'bg-purple-500',
      href: '/listings/verify'
    },
    {
      title: 'User Support',
      description: 'Assist users with platform issues',
      icon: Users,
      color: 'bg-orange-500',
      href: '/support'
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Staff Dashboard</h1>
              <p className="text-gray-600">Welcome back, {user?.full_name}</p>
            </div>
            <div className="flex items-center space-x-4">
              <span className="px-3 py-1 bg-blue-100 text-blue-800 text-sm font-medium rounded-full">
                Staff
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <Home className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Listings</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalListings}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="p-2 bg-yellow-100 rounded-lg">
                <Clock className="w-6 h-6 text-yellow-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Pending Visits</p>
                <p className="text-2xl font-bold text-gray-900">{stats.pendingVisits}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <MessageSquare className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Active Conversations</p>
                <p className="text-2xl font-bold text-gray-900">{stats.activeConversations}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <CheckCircle className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed Tasks</p>
                <p className="text-2xl font-bold text-gray-900">{stats.completedTasks}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="mb-8">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {quickActions.map((action, index) => (
              <button
                key={index}
                onClick={() => window.location.href = action.href}
                className="bg-white rounded-lg shadow-sm p-6 text-left hover:shadow-md transition-shadow"
              >
                <div className={`p-3 ${action.color} rounded-lg w-fit mb-4`}>
                  <action.icon className="w-6 h-6 text-white" />
                </div>
                <h3 className="font-semibold text-gray-900 mb-2">{action.title}</h3>
                <p className="text-sm text-gray-600">{action.description}</p>
              </button>
            ))}
          </div>
        </div>

        {/* Recent Activity */}
        <div className="bg-white rounded-lg shadow-sm">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-lg font-semibold text-gray-900">Recent Activity</h2>
          </div>
          <div className="p-6">
            <div className="space-y-4">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-green-100 rounded-full">
                  <CheckCircle className="w-4 h-4 text-green-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-900">Verified listing: Modern 2BHK in Thamel</p>
                  <p className="text-xs text-gray-500">2 hours ago</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-blue-100 rounded-full">
                  <Calendar className="w-4 h-4 text-blue-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-900">Scheduled visit for property in Baneshwor</p>
                  <p className="text-xs text-gray-500">4 hours ago</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-yellow-100 rounded-full">
                  <AlertCircle className="w-4 h-4 text-yellow-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-gray-900">New listing requires verification</p>
                  <p className="text-xs text-gray-500">6 hours ago</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StaffDashboardPage;
