import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { RootState } from '../store/store';

interface Visit {
  id: string;
  listingId: string;
  listingTitle: string;
  listingImage: string;
  ownerName: string;
  ownerPhone: string;
  scheduledDate: string;
  scheduledTime: string;
  status: 'pending' | 'confirmed' | 'completed' | 'cancelled';
  message?: string;
  createdAt: string;
}

const VisitsPage: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const [visits, setVisits] = useState<Visit[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState<'all' | 'pending' | 'confirmed' | 'completed' | 'cancelled'>('all');

  useEffect(() => {
    fetchVisits();
  }, []);

  const fetchVisits = async () => {
    setLoading(true);
    try {
      // TODO: Replace with actual API call
      // const response = await visitsAPI.getVisits();
      // setVisits(response.visits);
      
      // Mock data for now
      const mockVisits: Visit[] = [
        {
          id: '1',
          listingId: '1',
          listingTitle: 'Modern 2BR Apartment in Thamel',
          listingImage: 'https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?w=500',
          ownerName: 'Ram Shrestha',
          ownerPhone: '+977-1-4444444',
          scheduledDate: '2024-01-15',
          scheduledTime: '14:00',
          status: 'confirmed',
          message: 'I would like to see the apartment in the afternoon.',
          createdAt: '2024-01-10T00:00:00Z'
        },
        {
          id: '2',
          listingId: '2',
          listingTitle: 'Spacious 3BR House in Baneshwor',
          listingImage: 'https://images.unsplash.com/photo-1570129477492-45c003edd2be?w=500',
          ownerName: 'Sita Tamang',
          ownerPhone: '+977-1-5555555',
          scheduledDate: '2024-01-20',
          scheduledTime: '10:00',
          status: 'pending',
          message: 'Interested in viewing the house with my family.',
          createdAt: '2024-01-12T00:00:00Z'
        },
        {
          id: '3',
          listingId: '3',
          listingTitle: 'Cozy 1BR Studio in Patan',
          listingImage: 'https://images.unsplash.com/photo-1522708323590-d24dbb6b0267?w=500',
          ownerName: 'Hari Gurung',
          ownerPhone: '+977-1-6666666',
          scheduledDate: '2024-01-08',
          scheduledTime: '16:00',
          status: 'completed',
          message: 'Perfect for a student like me.',
          createdAt: '2024-01-05T00:00:00Z'
        }
      ];
      
      setVisits(mockVisits);
    } catch (error) {
      console.error('Error fetching visits:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'confirmed':
        return 'bg-blue-100 text-blue-800';
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'pending':
        return 'Pending';
      case 'confirmed':
        return 'Confirmed';
      case 'completed':
        return 'Completed';
      case 'cancelled':
        return 'Cancelled';
      default:
        return status;
    }
  };

  const filteredVisits = visits.filter(visit => {
    if (filter === 'all') return true;
    return visit.status === filter;
  });

  const cancelVisit = async (visitId: string) => {
    try {
      // TODO: Call API to cancel visit
      console.log('Cancelling visit:', visitId);
      // Update local state
      setVisits(prev => prev.map(visit => 
        visit.id === visitId ? { ...visit, status: 'cancelled' as const } : visit
      ));
    } catch (error) {
      console.error('Error cancelling visit:', error);
    }
  };

  const rescheduleVisit = async (visitId: string) => {
    // TODO: Implement reschedule functionality
    console.log('Rescheduling visit:', visitId);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex justify-center items-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">My Visits</h1>
          <p className="text-gray-600">
            Manage your scheduled property visits
          </p>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-blue-500 rounded-md flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                  </svg>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Total Visits</p>
                <p className="text-2xl font-semibold text-gray-900">{visits.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-yellow-500 rounded-md flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Pending</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {visits.filter(v => v.status === 'pending').length}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-blue-500 rounded-md flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Confirmed</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {visits.filter(v => v.status === 'confirmed').length}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <div className="w-8 h-8 bg-green-500 rounded-md flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                </div>
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Completed</p>
                <p className="text-2xl font-semibold text-gray-900">
                  {visits.filter(v => v.status === 'completed').length}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <div className="flex flex-wrap items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900 mb-4 sm:mb-0">Visit History</h2>
            <div className="flex space-x-2">
              {(['all', 'pending', 'confirmed', 'completed', 'cancelled'] as const).map((status) => (
                <button
                  key={status}
                  onClick={() => setFilter(status)}
                  className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                    filter === status
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {status.charAt(0).toUpperCase() + status.slice(1)}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Visits List */}
        {filteredVisits.length === 0 ? (
          <div className="bg-white rounded-lg shadow p-12 text-center">
            <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
            </svg>
            <h3 className="mt-2 text-sm font-medium text-gray-900">No visits found</h3>
            <p className="mt-1 text-sm text-gray-500">
              {filter === 'all' 
                ? "You haven't scheduled any visits yet."
                : `No ${filter} visits found.`
              }
            </p>
            {filter === 'all' && (
              <div className="mt-6">
                <Link
                  to="/listings"
                  className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
                >
                  Browse Properties
                </Link>
              </div>
            )}
          </div>
        ) : (
          <div className="space-y-6">
            {filteredVisits.map((visit) => (
              <div key={visit.id} className="bg-white rounded-lg shadow-md overflow-hidden">
                <div className="p-6">
                  <div className="flex items-start space-x-4">
                    {/* Property Image */}
                    <div className="flex-shrink-0">
                      <img
                        src={visit.listingImage}
                        alt={visit.listingTitle}
                        className="w-20 h-20 rounded-lg object-cover"
                      />
                    </div>

                    {/* Visit Details */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between mb-2">
                        <h3 className="text-lg font-semibold text-gray-900">
                          <Link to={`/listings/${visit.listingId}`} className="hover:text-blue-600">
                            {visit.listingTitle}
                          </Link>
                        </h3>
                        <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(visit.status)}`}>
                          {getStatusText(visit.status)}
                        </span>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                        <div>
                          <p className="text-sm text-gray-600">
                            <span className="font-medium">Owner:</span> {visit.ownerName}
                          </p>
                          <p className="text-sm text-gray-600">
                            <span className="font-medium">Phone:</span> {visit.ownerPhone}
                          </p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-600">
                            <span className="font-medium">Date:</span> {new Date(visit.scheduledDate).toLocaleDateString()}
                          </p>
                          <p className="text-sm text-gray-600">
                            <span className="font-medium">Time:</span> {visit.scheduledTime}
                          </p>
                        </div>
                      </div>

                      {visit.message && (
                        <div className="mb-4">
                          <p className="text-sm text-gray-600">
                            <span className="font-medium">Message:</span> {visit.message}
                          </p>
                        </div>
                      )}

                      {/* Actions */}
                      <div className="flex items-center space-x-3">
                        {visit.status === 'pending' && (
                          <>
                            <button
                              onClick={() => rescheduleVisit(visit.id)}
                              className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                            >
                              Reschedule
                            </button>
                            <button
                              onClick={() => cancelVisit(visit.id)}
                              className="text-red-600 hover:text-red-800 text-sm font-medium"
                            >
                              Cancel
                            </button>
                          </>
                        )}
                        
                        {visit.status === 'confirmed' && (
                          <>
                            <button
                              onClick={() => rescheduleVisit(visit.id)}
                              className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                            >
                              Reschedule
                            </button>
                            <button
                              onClick={() => cancelVisit(visit.id)}
                              className="text-red-600 hover:text-red-800 text-sm font-medium"
                            >
                              Cancel
                            </button>
                            <a
                              href={`tel:${visit.ownerPhone}`}
                              className="text-green-600 hover:text-green-800 text-sm font-medium"
                            >
                              Call Owner
                            </a>
                          </>
                        )}

                        {visit.status === 'completed' && (
                          <Link
                            to={`/listings/${visit.listingId}`}
                            className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                          >
                            View Property
                          </Link>
                        )}

                        <span className="text-sm text-gray-500">
                          Scheduled on {new Date(visit.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Upcoming Visits Alert */}
        {visits.filter(v => v.status === 'confirmed' && new Date(v.scheduledDate) > new Date()).length > 0 && (
          <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-6">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-blue-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-blue-800">
                  Upcoming Visits
                </h3>
                <div className="mt-2 text-sm text-blue-700">
                  <p>
                    You have {visits.filter(v => v.status === 'confirmed' && new Date(v.scheduledDate) > new Date()).length} confirmed visit(s) coming up. 
                    Make sure to arrive on time and bring any necessary documents.
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default VisitsPage;
