import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../store/store';
import ChatInterface from '../components/messaging/ChatInterface';
import NewConversationButton from '../components/messaging/NewConversationButton';
import { MessageSquare, Search, Plus, Bell, Filter } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import messagingService from '../services/messagingService';
import { toast } from 'react-hot-toast';

interface Conversation {
  conversation_id: string;
  listing_id: string;
  listing_title: string;
  listing_price: number;
  owner_id: string;
  other_participant_name: string;
  other_participant_id: string;
  other_participant_avatar?: string;
  other_participant_role?: string;
  last_message?: string;
  last_message_time?: string;
  last_message_sender_id?: string;
  unread_count: number;
}

const MessagesPage: React.FC = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConversation, setSelectedConversation] = useState<Conversation | null>(null);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<'all' | 'unread' | 'owners' | 'tenants'>('all');

  useEffect(() => {
    fetchConversations();
    setupRealTimeUpdates();
  }, []);

  const setupRealTimeUpdates = () => {
    messagingService.connect();

    // Handle new messages to update conversation list
    const unsubscribeMessage = messagingService.onNewMessage((message) => {
      // Update the conversation in the list
      setConversations(prev => 
        prev.map(conv => 
          conv.conversation_id === message.conversation_id
            ? {
                ...conv,
                last_message: message.message_text,
                last_message_time: message.created_at,
                last_message_sender_id: message.sender_id,
                unread_count: conv.unread_count + (message.sender_id !== user?.user_id ? 1 : 0)
              }
            : conv
        )
      );
    });

    return () => {
      unsubscribeMessage();
    };
  };

  const fetchConversations = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/messages/conversations', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setConversations(data.conversations || []);
      }
    } catch (error) {
      console.error('Error fetching conversations:', error);
      toast.error('Failed to load conversations');
    } finally {
      setLoading(false);
    }
  };

  const filteredConversations = conversations.filter(conv => {
    const matchesSearch = conv.other_participant_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         conv.listing_title.toLowerCase().includes(searchTerm.toLowerCase());
    
    if (!matchesSearch) return false;
    
    switch (filterType) {
      case 'unread':
        return conv.unread_count > 0;
      case 'owners':
        return conv.other_participant_role === 'owner';
      case 'tenants':
        return conv.other_participant_role === 'tenant';
      default:
        return true;
    }
  });

  const formatLastMessageTime = (timestamp?: string) => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    const now = new Date();
    const diffInHours = (now.getTime() - date.getTime()) / (1000 * 60 * 60);

    if (diffInHours < 24) {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else if (diffInHours < 168) { // 7 days
      return date.toLocaleDateString([], { weekday: 'short' });
    } else {
      return date.toLocaleDateString([], { month: 'short', day: 'numeric' });
    }
  };

  const handleConversationSelect = (conversation: Conversation) => {
    setSelectedConversation(conversation);
    
    // Mark conversation as read when selected
    if (conversation.unread_count > 0) {
      markConversationAsRead(conversation.conversation_id);
    }
  };

  const markConversationAsRead = async (conversationId: string) => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:5000/api/messages/conversations/${conversationId}/read`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      // Update local state
      setConversations(prev => 
        prev.map(conv => 
          conv.conversation_id === conversationId
            ? { ...conv, unread_count: 0 }
            : conv
        )
      );
    } catch (error) {
      console.error('Error marking conversation as read:', error);
    }
  };

  const getTotalUnreadCount = () => {
    return conversations.reduce((total, conv) => total + conv.unread_count, 0);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50">
      {/* Header */}
      <motion.div 
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white shadow-lg border-b border-gray-200"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                Messages
              </h1>
              {getTotalUnreadCount() > 0 && (
                <div className="bg-red-500 text-white text-sm px-3 py-1 rounded-full font-medium">
                  {getTotalUnreadCount()} new
                </div>
              )}
            </div>
            <div className="flex items-center space-x-3">
              <button 
                onClick={() => {
                  // This will be handled by the floating action button
                  toast.info('Use the + button to start a new conversation');
                }}
                className="flex items-center px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg hover:from-blue-700 hover:to-purple-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:scale-105"
              >
                <Plus className="w-4 h-4 mr-2" />
                New Chat
              </button>
            </div>
          </div>
        </div>
      </motion.div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[calc(100vh-200px)]">
          {/* Conversations List */}
          <motion.div 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-1 bg-white rounded-xl shadow-lg border border-gray-200"
          >
            <div className="p-4 border-b border-gray-200">
              <div className="relative mb-4">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search conversations..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                />
              </div>
              
              {/* Filter Tabs */}
              <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg">
                {[
                  { key: 'all', label: 'All', count: conversations.length },
                  { key: 'unread', label: 'Unread', count: conversations.filter(c => c.unread_count > 0).length },
                  { key: 'owners', label: 'Owners', count: conversations.filter(c => c.other_participant_role === 'owner').length },
                  { key: 'tenants', label: 'Tenants', count: conversations.filter(c => c.other_participant_role === 'tenant').length }
                ].map((filter) => (
                  <button
                    key={filter.key}
                    onClick={() => setFilterType(filter.key as any)}
                    className={`flex-1 px-3 py-2 text-sm font-medium rounded-md transition-all duration-200 ${
                      filterType === filter.key
                        ? 'bg-white text-blue-600 shadow-sm'
                        : 'text-gray-600 hover:text-gray-900'
                    }`}
                  >
                    {filter.label}
                    {filter.count > 0 && (
                      <span className="ml-1 text-xs bg-gray-200 text-gray-600 px-2 py-1 rounded-full">
                        {filter.count}
                      </span>
                    )}
                  </button>
                ))}
              </div>
            </div>

            <div className="overflow-y-auto h-[calc(100%-140px)]">
              {loading ? (
                <div className="flex justify-center items-center h-full">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
                </div>
              ) : filteredConversations.length === 0 ? (
                <div className="flex flex-col items-center justify-center h-full text-gray-500 p-8">
                  <MessageSquare className="w-16 h-16 mb-4 text-gray-300" />
                  <p className="text-lg font-medium mb-2">No conversations found</p>
                  <p className="text-sm text-center">
                    {searchTerm || filterType !== 'all' 
                      ? 'Try adjusting your search or filters'
                      : 'Start a conversation about a property'
                    }
                  </p>
                </div>
              ) : (
                <div className="divide-y divide-gray-100">
                  <AnimatePresence>
                    {filteredConversations.map((conversation, index) => (
                      <motion.button
                        key={conversation.conversation_id}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: index * 0.05 }}
                        onClick={() => handleConversationSelect(conversation)}
                        className={`w-full p-4 text-left hover:bg-gray-50 transition-all duration-200 ${
                          selectedConversation?.conversation_id === conversation.conversation_id
                            ? 'bg-blue-50 border-r-4 border-blue-600'
                            : ''
                        }`}
                      >
                        <div className="flex items-start space-x-3">
                          <div className="relative">
                            <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-medium flex-shrink-0">
                              {conversation.other_participant_avatar ? (
                                <img
                                  src={conversation.other_participant_avatar}
                                  alt={conversation.other_participant_name}
                                  className="w-12 h-12 rounded-full object-cover"
                                />
                              ) : (
                                conversation.other_participant_name.charAt(0).toUpperCase()
                              )}
                            </div>
                            {conversation.unread_count > 0 && (
                              <div className="absolute -top-1 -right-1 bg-red-500 text-white text-xs px-2 py-1 rounded-full min-w-[20px] text-center">
                                {conversation.unread_count}
                              </div>
                            )}
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-1">
                              <h3 className="font-semibold text-gray-900 truncate">
                                {conversation.other_participant_name}
                              </h3>
                              <span className="text-xs text-gray-500 flex-shrink-0">
                                {formatLastMessageTime(conversation.last_message_time)}
                              </span>
                            </div>
                            <p className="text-sm text-gray-600 truncate mb-1">
                              {conversation.listing_title}
                            </p>
                            <div className="flex items-center justify-between">
                              {conversation.last_message && (
                                <p className="text-sm text-gray-500 truncate flex-1">
                                  <span className={conversation.last_message_sender_id === user?.user_id ? 'text-blue-600 font-medium' : ''}>
                                    {conversation.last_message_sender_id === user?.user_id ? 'You: ' : ''}
                                  </span>
                                  {conversation.last_message}
                                </p>
                              )}
                              <div className="flex items-center space-x-2 ml-2">
                                <span className={`text-xs px-2 py-1 rounded-full ${
                                  conversation.other_participant_role === 'owner' 
                                    ? 'bg-green-100 text-green-700' 
                                    : 'bg-blue-100 text-green-700'
                                }`}>
                                  {conversation.other_participant_role === 'owner' ? 'Owner' : 'Tenant'}
                                </span>
                                <span className="text-sm font-medium text-gray-900">
                                  â‚¹{conversation.listing_price.toLocaleString()}
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      </motion.button>
                    ))}
                  </AnimatePresence>
                </div>
              )}
            </div>
          </motion.div>

          {/* Chat Interface */}
          <motion.div 
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-2 bg-white rounded-xl shadow-lg border border-gray-200"
          >
            {selectedConversation ? (
              <ChatInterface
                conversationId={selectedConversation.conversation_id}
                otherParticipant={{
                  name: selectedConversation.other_participant_name,
                  avatar: selectedConversation.other_participant_avatar,
                  id: selectedConversation.other_participant_id,
                  role: selectedConversation.other_participant_role
                }}
                listingId={selectedConversation.listing_id}
              />
            ) : (
              <div className="flex flex-col items-center justify-center h-full text-gray-500 p-8">
                <MessageSquare className="w-20 h-20 mb-6 text-gray-300" />
                <h3 className="text-xl font-semibold mb-3 text-gray-700">Select a conversation</h3>
                <p className="text-center text-gray-500 max-w-md">
                  Choose a conversation from the list to start messaging. 
                  You can search, filter, and manage all your property-related conversations here.
                </p>
              </div>
            )}
          </motion.div>
        </div>
      </div>

      {/* Floating Action Button */}
      <NewConversationButton />
    </div>
  );
};

export default MessagesPage;
