import React from 'react';
import { GoogleLogin } from 'react-google-login';
import { authService } from '../../services/authService';

interface SSOButtonsProps {
  onSuccess: (user: any, provider: string) => void;
  onError: (error: string) => void;
  disabled?: boolean;
}

const SSOButtons: React.FC<SSOButtonsProps> = ({ onSuccess, onError, disabled = false }) => {
  const { instance } = useMsal();

  // Google OAuth configuration
  const googleClientId = process.env.REACT_APP_GOOGLE_CLIENT_ID || 'your-google-client-id';

  // Facebook OAuth configuration
  const facebookAppId = process.env.REACT_APP_FACEBOOK_APP_ID || 'your-facebook-app-id';

  // Microsoft OAuth configuration
  const microsoftClientId = process.env.REACT_APP_MICROSOFT_CLIENT_ID || 'your-microsoft-client-id';

  // Google OAuth success handler
  const handleGoogleSuccess = async (response: any) => {
    try {
      // Send the Google token to backend for verification
      const backendResponse = await fetch('/api/auth/google/callback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          token: response.tokenId,
          profile: response.profileObj
        })
      });

      if (backendResponse.ok) {
        const data = await backendResponse.json();
        authService.setToken(data.token);
        onSuccess(data.user, 'google');
      } else {
        onError('Google authentication failed');
      }
    } catch (error) {
      console.error('Google OAuth error:', error);
      onError('Google authentication failed');
    }
  };

  // Google OAuth failure handler
  const handleGoogleFailure = (error: any) => {
    console.error('Google OAuth failure:', error);
    onError('Google authentication failed');
  };

  // Facebook OAuth success handler
  const handleFacebookSuccess = async (response: any) => {
    try {
      // Send the Facebook token to backend for verification
      const backendResponse = await fetch('/api/auth/facebook/callback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          token: response.accessToken,
          profile: response
        })
      });

      if (backendResponse.ok) {
        const data = await backendResponse.json();
        authService.setToken(data.token);
        onSuccess(data.user, 'facebook');
      } else {
        onError('Facebook authentication failed');
      }
    } catch (error) {
      console.error('Facebook OAuth error:', error);
      onError('Facebook authentication failed');
    }
  };

  // Facebook OAuth failure handler
  const handleFacebookFailure = (error: any) => {
    console.error('Facebook OAuth failure:', error);
    onError('Facebook authentication failed');
  };

  // Microsoft OAuth handler
  const handleMicrosoftLogin = async () => {
    try {
      const loginRequest = {
        scopes: ['user.read'],
        prompt: 'select_account',
      };

      const response = await instance.loginPopup(loginRequest);

      if (response) {
        // Send the Microsoft token to backend for verification
        const backendResponse = await fetch('/api/auth/microsoft/callback', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            token: response.accessToken,
            profile: response.account
          })
        });

        if (backendResponse.ok) {
          const data = await backendResponse.json();
          authService.setToken(data.token);
          onSuccess(data.user, 'microsoft');
        } else {
          onError('Microsoft authentication failed');
        }
      }
    } catch (error) {
      console.error('Microsoft OAuth error:', error);
      onError('Microsoft authentication failed');
    }
  };

  return (
    <div className="space-y-4">
      <div className="text-center text-sm text-gray-600 mb-4">
        Or continue with
      </div>

      <div className="space-y-3">
        {/* Google Login */}
        <GoogleLogin
          clientId={googleClientId}
          buttonText="Continue with Google"
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
          cookiePolicy={'single_host_origin'}
          disabled={disabled}
          render={(renderProps) => (
            <button
              onClick={renderProps.onClick}
              disabled={renderProps.disabled || disabled}
              className="w-full flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
                <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              Continue with Google
            </button>
          )}
        />

        {/* Facebook Login */}
        <FacebookLogin
          appId={facebookAppId}
          fields="name,email,picture"
          callback={handleFacebookSuccess}
          onFailure={handleFacebookFailure}
          disabled={disabled}
          render={(renderProps) => (
            <button
              onClick={renderProps.onClick}
              disabled={disabled}
              className="w-full flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <svg className="w-5 h-5 mr-2" fill="#1877F2" viewBox="0 0 24 24">
                <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
              </svg>
              Continue with Facebook
            </button>
          )}
        />

        {/* Microsoft Login */}
        <button
          onClick={handleMicrosoftLogin}
          disabled={disabled}
          className="w-full flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md shadow-sm bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <svg className="w-5 h-5 mr-2" fill="#00BCF2" viewBox="0 0 24 24">
            <path d="M11.4 24H0V12.6h11.4V24zM24 24H12.6V12.6H24V24zM11.4 11.4H0V0h11.4v11.4zM24 11.4H12.6V0H24v11.4z"/>
          </svg>
          Continue with Microsoft
        </button>
      </div>
    </div>
  );
};

export default SSOButtons;
