import { useState, useEffect } from 'react';

interface VisitData {
  visitCount: number;
  lastVisitTime: number;
  hasSeenAd: boolean;
}

const VISIT_THRESHOLD = 3;
const AD_COOLDOWN = 24 * 60 * 60 * 1000; // 24 hours in milliseconds

export const useVisitTracker = () => {
  const [visitData, setVisitData] = useState<VisitData>({
    visitCount: 0,
    lastVisitTime: 0,
    hasSeenAd: false
  });
  const [showPremiumAd, setShowPremiumAd] = useState(false);

  useEffect(() => {
    // Load visit data from localStorage
    const savedData = localStorage.getItem('visitTracker');
    if (savedData) {
      try {
        const parsed = JSON.parse(savedData);
        setVisitData(parsed);
      } catch (error) {
        console.error('Error parsing visit data:', error);
      }
    }
  }, []);

  const trackVisit = () => {
    const now = Date.now();
    const newVisitData = {
      visitCount: visitData.visitCount + 1,
      lastVisitTime: now,
      hasSeenAd: visitData.hasSeenAd
    };

    setVisitData(newVisitData);
    localStorage.setItem('visitTracker', JSON.stringify(newVisitData));

    // Check if we should show the premium ad
    if (
      newVisitData.visitCount >= VISIT_THRESHOLD &&
      !newVisitData.hasSeenAd &&
      (now - visitData.lastVisitTime) > AD_COOLDOWN
    ) {
      setShowPremiumAd(true);
    }
  };

  const markAdAsSeen = () => {
    const updatedData = {
      ...visitData,
      hasSeenAd: true
    };
    setVisitData(updatedData);
    localStorage.setItem('visitTracker', JSON.stringify(updatedData));
    setShowPremiumAd(false);
  };

  const skipAd = () => {
    setShowPremiumAd(false);
    // Reset the ad flag after cooldown period
    setTimeout(() => {
      const resetData = {
        ...visitData,
        hasSeenAd: false
      };
      setVisitData(resetData);
      localStorage.setItem('visitTracker', JSON.stringify(resetData));
    }, AD_COOLDOWN);
  };

  const resetVisitCount = () => {
    const resetData = {
      visitCount: 0,
      lastVisitTime: Date.now(),
      hasSeenAd: false
    };
    setVisitData(resetData);
    localStorage.setItem('visitTracker', JSON.stringify(resetData));
  };

  return {
    visitCount: visitData.visitCount,
    showPremiumAd,
    trackVisit,
    markAdAsSeen,
    skipAd,
    resetVisitCount
  };
};
